    private void stdButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_stdButtonActionPerformed
        // TODO add your handling code here:
      String price = "₱: ";

    // Check which radio button is selected and set the price accordingly
    if (smallCottage.isSelected()) {
        price += " 1000"; // Price for Single Room
    } else if (mediumCottage.isSelected()) {
        price += " 2500"; // Price for Double Room
    } else if (largeCottage.isSelected()) {
        price += " 8000"; // Price for Twin Room
    } 
    else if (premiumRoom.isSelected()) {
        price += " 6000"; // Price for Twin Room
    } 
    else if (balconyRoom.isSelected()) {
        price += " 6500"; // Price for Twin Room
    } 
    else if (viewRoom.isSelected()) {
        price += " 7000"; // Price for Twin Room
    }
    else if (juniorRoom.isSelected()) {
        price += " 8000"; // Price for Twin Room
    }
    else if (exeRoom.isSelected()) {
        price += " 8500"; // Price for Twin Room
    }
    else if (presRoom.isSelected()) {
        price += " 9000"; // Price for Twin Room
    }
    else if (royalRoom.isSelected()) {
        price += " 9500"; // Price for Twin Room
    }
     else {
        price += " 0"; // Default if no room is selected
    }
     stdPrice.setText(price);
  
    }//GEN-LAST:event_stdButtonActionPerformed

