    private void cottageButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cottageButtonActionPerformed
         String price = "₱: ";
    int durationMultiplier = 1; // Default multiplier for price

    // Check the selected duration from the combo box
    String selectedDuration = (String) txtbox.getSelectedItem();
    if (selectedDuration.equals("24 hours")) {
        durationMultiplier = 2; // Double the price for 24 hours
    }

    // Check which radio button is selected and set the price accordingly
    if (smallCottage.isSelected()) {
        price += (700 * durationMultiplier); // Price for Small Cottage
    } else if (mediumCottage.isSelected()) {
        price += (900 * durationMultiplier); // Price for Medium Cottage
    } else if (largeCottage.isSelected()) {
        price += (1400 * durationMultiplier); // Price for Large Cottage
    } else {
        price += "0"; // Default if no cottage is selected
    }

    // Set the calculated price in the cottagePrice text field
    cottagePrice.setText(price);
    }//GEN-LAST:event_cottageButtonActionPerformed

