    private void completeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_completeButtonActionPerformed
       // Retrieve user inputs
    String name = txtName.getText();
    String number = txtNumber.getText();
    String date = txtdate.getText();
    String stdPriceValue = stdPrice.getText().replace("₱: ", "").trim(); // Remove currency symbol and whitespace
    String cottagePriceValue = cottagePrice.getText().replace("₱: ", "").trim(); // Remove currency symbol and whitespace

    // Convert prices to integers for calculation
    int stdPriceInt = Integer.parseInt(stdPriceValue);
    int cottagePriceInt = Integer.parseInt(cottagePriceValue);

    // Calculate total price
    int totalPrice = stdPriceInt + cottagePriceInt;

    // Format the receipt
    String receipt = "Here's your Receipt!\n";
    receipt += "=====================\n";
    receipt += "Name: " + name + "\n";
    receipt += "Number: " + number + "\n";
    receipt += "=====================\n";
    receipt += "Date of Reservation: " + date + "\n";
    receipt += "Standard Room Price: ₱" + stdPriceValue + "\n";
    receipt += "Cottage Price: ₱" + cottagePriceValue + "\n";
     receipt += "=====================\n";
    receipt += "Total Price: ₱" + totalPrice + "\n"; // Display total price
    receipt += "=====================\n";

    // Display the receipt in the allReceipt text area
    allReceipt.setText(receipt);
    }//GEN-LAST:event_completeButtonActionPerformed

